package com.basics.program;

public class numberminus {

	public static void main(String[] args) {
		int a=0,s=0;
		// TODO Auto-generated method stub
		
     for(int i=1;i<=10;i++)
     {
    	 
    	if(i%2==0)
    	{
    		a=i*2;
    		s=i-a;
    		System.out.print(" "+s);
    		
    	}else
    	System.out.print(" "+i);
     }
     
	}

}
